let toasts={};let toastParent;let styles;function proxy(context,method){return function(){if(toastParent){toast(method.name,arguments)}method.apply(context,arguments)}}console.log=proxy(console,console.log);console.info=proxy(console,console.info);console.debug=proxy(console,console.debug);console.warn=proxy(console,console.warn);console.error=proxy(console,console.error);function toast(level,message){let temp=document.createElement("div");let closeButton=document.createElement("button");closeButton.addEventListener("click",(ev=>{const id=Number(ev.target.parentElement.id);const ele=toasts[id];toastParent.removeChild(ele);delete toasts[id]}));closeButton.textContent="X";temp.textContent=stringify(message[0]);temp.appendChild(closeButton);temp.id=Object.keys(toasts).length;switch(level){case"debug":temp.style.backgroundColor="blue";temp.style.color="white";break;case"warn":temp.style.backgroundColor="yellow";break;case"log":case"info":temp.style.backgroundColor="lightgrey";break;case"error":temp.style.backgroundColor="red";temp.style.color="white";break;default:}toasts[temp.id]=temp;toastParent.appendChild(temp)}function indent(s,padding){return s.split("\n").map((function(s){return(padding||"\t")+s})).join("\n")}function stringify(anything){var p,key_values=[];if(anything instanceof Array){return"["+anything.map(stringify).join(", ")+"]"}if(anything instanceof Date){return anything.toString()}if(typeof anything==="object"){for(p in anything){if(anything.hasOwnProperty(p)){key_values.push(p+": "+stringify(anything[p]))}}return"{\n"+indent(key_values.join(",\n"),"    ")+"\n}"}if(typeof anything==="string"){return'"'+anything+'"'}return anything.toString()}window.onload=()=>{toastParent=document.createElement("div");styles=document.createElement("style");styles.type="text/css";styles.innerHTML=`\n        #toast-parent {\n            position: absolute;\n            top: 0;\n            right: 0;\n            min-height: 100vh;\n            margin: 0.5rem;\n            max-width: 300px;\n        }\n\n        #toast-parent > * {\n            display: flex;\n            justify-content: space-between;\n            padding: 0.5rem 1rem;\n            font-size: 16px;\n            font-family: monospace;\n            opacity: 0;\n            position: relative;\n            margin: 0.5rem 0;\n            min-height: 25px;\n            width: 250px;\n            border-radius: 8px;\n            transition: all 0.3s ease;\n            animation: insert 0.3s ease forwards;\n        }\n\n        @keyframes insert {\n            from {\n                opacity: 0;\n            }\n            to {\n                opacity: 0.75;\n            }\n        }\n\n        #toast-parent button {\n            cursor: pointer;\n            height: 25px;\n            width: 25px;\n        }\n    `;toastParent.id="toast-parent";document.head.appendChild(styles);document.body.appendChild(toastParent)};
